import React, {useState} from 'react';
import axios from 'axios'; 
    function Post() {
      const url = 'http://localhost:8000/api/clientes'
      const [data, setData] = useState({
        nombre: "",
        apellido: "",
        cedula: ""
      })
      function submit(e){
       e.preventDefault();
       axios.post(url,{
         nombre: data.nombre,
         apellido: data.apellido,
         cedula: data.cedula
       })
       .then(res=>{
         console.log(res.data)
       })
      }
      function handle(e){
       const newdata = { ...data }
       newdata[e.target.id] = e.target.value
       setData(newdata)
       console.log(newdata)
      }
        return (
<div class="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
  <div class="relative py-3 w-11/12 max-w-xl sm:mx-auto">
    <div class="relative p-8 bg-white shadow-sm sm:rounded-xl">
      <form  class="w-full">
        <div class="mb-5 relative">
          <input  value={data.nombre} type="text" id="nombre" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="name@example.com" autocomplete="off" />
          <label for="nombre" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Nombre</label>
        </div>
        <div class="mb-5 relative">
          <input  value={data.apellido} type="text" id="apellido" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="password" autocomplete="off" />
          <label for="apellido" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Apellido</label>
        </div>
        <div class="mb-5 relative">
          <input  value={data.cedula} type="text" id="cedula" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="password" autocomplete="off" />
          <label for="cedula" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Numero De Cédula</label>
        </div>
        <button class="w-full bg-yellow-400 text-white p-3 rounded-md">Enviar</button>
      </form>
    </div>
  </div>
</div>
        );
    }
export default Post;
