import React, {useState} from 'react';
import axios from 'axios'; 
    function Post() {
      const url = 'http://localhost:8000/api/clientes'
      const [data, setData] = useState({
        nombre: "",
        apellido: "",
        cedula: ""
      })
      function submit(e){
       e.preventDefault();
       axios.post(url,{
         nombre: data.nombre,
         apellido: data.apellido,
         cedula: data.cedula
       })
       .then(res=>{
         console.log(res.data)
       })
      }
      function handle(e){
       const newdata = { ...data }
       newdata[e.target.id] = e.target.value
       setData(newdata)
       console.log(newdata)
      }
        return (
<div class="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
  <div class="relative py-3 w-11/12 max-w-xl sm:mx-auto">
    <div class="relative p-8 bg-white shadow-sm sm:rounded-xl">
      <form onSubmit={(e)=> submit(e)} class="w-full">
          <input onChange={(e)=handle(e)} value={data.nombre} type="text" id="nombre"  />
          <input onChange={(e)=handle(e)} value={data.apellido} type="text" id="apellido"/>
          <input onChange={(e)=handle(e)} value={data.cedula} type="text" id="cedula"  />
        <button class="w-full bg-yellow-400 text-white p-3 rounded-md">Enviar</button>
      </form>
    </div>
  </div>
</div>
        );
    }
export default Post;
