      cedula: ''
    }
  }
  changeHandler = (e) => {
    this.setState({[e.target.name]: e.target.value })
  }
  submitHandler = e=> {
    e.preventDefault()
    console.log(this.state)
    axios.post('http://localhost:8000/api/clientes', this.state)
    .then(response => {
      console.log(response)
    })
    .catch(error => {
      console.log(error)
    })
  }
