        </div>
        <div class="mb-5 relative">
          <input onChange={this.changeHandler}  value={cedula} type="text" id="cedula" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="password" autocomplete="off" />
