import React, {Component} from 'react';
    
class Post extends Component{
  constructor(props) {
    super(props);

    this.state = {
        nombre: '',
        apellido: '',
        cedula: '',
    };

    this.handleChangeNombre= this.handleChangeNombre.bind(this);
    this.handleChangeApellido= this.handleChangeApellido.bind(this);
    this.handleChangeCedula= this.handleChangeCedula.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

}


handleChangeNombre(event) {
    this.setState({
        nombre: event.target.value,
    })
}

handleChangeApellido(event) {
    this.setState({
        apellido: event.target.value,
    })
}

handleChangeCedula(event) {
    this.setState({
        cedula: event.target.value,
    })
}


handleSubmit(event) {
    fetch('http://localhost:8000/api/clientes', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            nombre : this.state.nombre,
            apellido : this.state.apellido,
            cedula : this.state.cedula,
        })
    });

    event.preventDefault();
}

    render() {
        return ( 
<div class="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
  <div class="relative py-3 w-11/12 max-w-xl sm:mx-auto">
    <div class="relative p-8 bg-white shadow-sm sm:rounded-xl">
      <form onSubmit={this.submitHandler} class="w-full">
        <div class="mb-5 relative" >
          <input onChange={this.handleChangeNombre} value={this.state.nombre} type="text" id="nombre" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="name@example.com" autocomplete="off" />
          <label for="nombre" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Nombre</label>
        </div>
        <div class="mb-5 relative">
          <input onChange={this.handleChangeApellido} value={this.state.apellido} type="text" id="apellido" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="password" autocomplete="off" />
          <label for="apellido" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Apellido</label>
        </div>
        <div class="mb-5 relative">
          <input onChange={this.handleChangeCedula} value={this.state.cedula} type="text" id="cedula" class="peer pt-8 border border-gray-200 focus:outline-none rounded-md focus:border-gray-500 focus:shadow-sm w-full p-3 h-16 placeholder-transparent" placeholder="password" autocomplete="off" />
          <label for="cedula" class="peer-placeholder-shown:opacity-100   opacity-75 peer-focus:opacity-75 peer-placeholder-shown:scale-100 scale-75 peer-focus:scale-75 peer-placeholder-shown:translate-y-0 -translate-y-3 peer-focus:-translate-y-3 peer-placeholder-shown:translate-x-0 translate-x-1 peer-focus:translate-x-1 absolute top-0 left-0 px-3 py-5 h-full pointer-events-none transform origin-left transition-all duration-100 ease-in-out">Numero De Cédula</label>
        </div>
        <button type="submit" class="w-full bg-yellow-400 text-white p-3 rounded-md">Enviar</button>
      </form>
    </div>
  </div>
</div>
        );
    }
  }
export default Post;
