import React, {Component} from 'react';
    
class Post extends Component{
  constructor(props) {
    super(props);

    this.state = {
        nombre: '',
        apellido: '',
        cedula: '',
    };

    this.handleChangeNombre= this.handleChangeNombre.bind(this);
    this.handleChangeApellido= this.handleChangeApellido.bind(this);
    this.handleChangeCedula= this.handleChangeCedula.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);

}


handleChangeNombre(event) {
    this.setState({
        nombre: event.target.value,
    })
}

handleChangeApellido(event) {
    this.setState({
        apellido: event.target.value,
    })
}

handleChangeCedula(event) {
    this.setState({
        cedula: event.target.value,
    })
}


handleSubmit(event) {
    fetch('http://localhost:8000/api/clientes', {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json',
        },
        body: JSON.stringify({
            nombre : this.state.nombre,
            apellido : this.state.apellido,
            cedula : this.state.cedula,
        })
    });

    event.preventDefault();
}


render() {

  var divStyle = {
      color: 'red',
      margin: '150px 150px 160px 350px',
  };

  return (
      <div style={divStyle}>
          <Form>
              <Form.Group controlId="formBasicSuplementos">
                  <Form.Check id="suplementos" type="checkbox" label="Suplementos" />
              </Form.Group>

              <Form.Group controlId="formBasicWeight">
                  <Form.Label>Nombre</Form.Label>
                  <Form.Control id="weight" name="weight" placeholder="weight" onChange={this.handleChangeNombre} />
              </Form.Group>

              <Form.Group controlId="formBasicHeight">
                  <Form.Label>Apellido</Form.Label>
                  <Form.Control id="height" name="height" placeholder="height" onChange={this.handleChangeApellido} />
              </Form.Group>


              <Form.Group controlId="formBasicImc">
                  <Form.Label>Cedula</Form.Label>
                  <Form.Control id="imc" name="imc" placeholder="imc" onChange={this.handleChangeCedula} />
              </Form.Group>

              <Button onClick={this.handleSubmit}>Enviar</Button>
          </Form>
      </div>
  );
}
}
export default Post;
